  "items": [
    {
      "type": "SectionHeaderItem",
      "text": "Machine identification"
    },
    {
      "type": "TextItem",
      "text": "In order to provide you with good service various details about your machine may sometimes be required. Please complete these details here in order to have them at hand when they are requested."
    },
    {
      "type": "TextItem",
      "text": "Type of machine"
    },
    {
      "type": "TextItem",
      "text": "Exacta-TLX-GEOSPREAD GS3"
    },
    {
      "type": "TextItem",
      "text": "Serial number"
    },
    {
      "type": "TextItem",
      "text": "Options"
    },
    {
