    {
      "type": "SectionHeaderItem",
      "text": "\u00b7 Extra functions:"
    },
    {
      "type": "ListItem",
      "text": "\u00b7 Calculator"
    },
    {
      "type": "ListItem",
      "text": "\u00b7 Internet browser"
