    },
    {
      "type": "SectionHeaderItem",
      "text": "IsoMatch InLine"
    },
    {
      "type": "PictureItem",
      "caption": ""
    },
    {
      "type": "SectionHeaderItem",
      "text": "Globale GPS-Antenne IsoMatch"
    },
    {
      "type": "PictureItem",
      "caption": ""
    },
    {
      "type": "SectionHeaderItem",
      "text": "IsoMatch Wireless"
    },
    {
